pub enum WildFlyServerStatusPodsState {
    #[serde(rename = "ACTIVE")]
    Active,
    #[serde(rename = "SCALING_DOWN_RECOVERY_INVESTIGATION")]
    ScalingDownRecoveryInvestigation,
    #[serde(rename = "SCALING_DOWN_RECOVERY_PROCESSING")]
    ScalingDownRecoveryProcessing,
    #[serde(rename = "SCALING_DOWN_RECOVERY_HEURISTICS")]
    ScalingDownRecoveryHeuristics,
    #[serde(rename = "SCALING_DOWN_CLEAN")]
    ScalingDownClean,
}

